% Take the .mat files and create a .csv out of them.  This will% then get loaded into PostgreSQLfunction [] = mat_to_csv()	fid = fopen('eddies.csv', 'w');	fprintf(fid, 'track_id,lat,lon,date,surface_area,amplitude,radius,mean_geo_speed\n');	files = {'anticyc_simple.mat', 'cyclonic_simple.mat'};	next_id = 1;	for i = 1:length(files)		disp(['Processing ' files{i}])		load(['data/' files{i}]);		next_id = write_data(s_tracks, fid, next_id);	endendfunction [next_id] = write_data(data, fid, next_id)	for i = 1:length(data)  		track = data{i};  		for j = 1:size(track, 1)  			fprintf(fid, '%d,%f,%f,%d,%f,%f,%f,%f\n', [next_id, track(j, :)]);  		end  		next_id = next_id + 1;  	endend